import React, { useState } from 'react';
import styles from './loginForm.module.css'
import { login, logout } from '../../contexts/auth';
import { useNavigate } from 'react-router-dom';

const LoginForm = () => {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const history = useNavigate();

  const handleEmailChange = (event) => {
    setName(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const token = await login(name, password);
      localStorage.setItem('token', token);
      history.push('/');
    } catch (err) {
      console.error(err.message);
      // Display error message to user
    }
  };
  const handleLogout = () => {
    logout();
    setLoggedIn(false);
    history.push('/');

  };

  return (
    <div>
      {loggedIn ? (
        <button onClick={handleLogout}>Logout</button>
      ) : (
        <div className={styles.loginbox}>
          <div className="container">
            <form className={styles.login_content} onSubmit={handleSubmit}  >
              <h1 className={styles.text}>Кирүү</h1>
              <div>
                <label htmlFor="email"></label>
                <input
                  type="text"
                  id="email"
                  name="email"
                  value={name}
                  onChange={handleEmailChange}
                  required
                  placeholder='Электрондук почта'
                />
              </div>
              <div>
                <label htmlFor="password"></label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={password}
                  onChange={handlePasswordChange}
                  required
                  placeholder='Сырсөз'
                />
              </div>
              <button className={styles.btn1} type="submit">Кирүү</button>
            </form>
          </div>
        </div>
      )}

    </div>
  );
};

export default LoginForm;
