import React, { useEffect } from 'react';

const GoogleTranslateWidget = () => {
  useEffect(() => {
    new window.google.translate.TranslateElement({
      pageLanguage: 'kg',
      includedLanguages: 'ar,bn,es, en, fr,hi,id,ja,ko,ms,pt,ru,th,tr,vi,zh-CN,zh-TW',
      layout: window.google.translate.TranslateElement.InlineLayout.SIMPLE,
      autoDisplay: false,
      gaTrack: true,
      gaId: 'UA-123456-1',
    }, 'google_translate_element');

    // Style the Google Translate widget
    const widget = document.querySelector('#google_translate_element');
    if (widget) {
      // Set background color and padding
      widget.setAttribute('style', 'background-color: #fff; padding: 20px;');

      // Style the language dropdown button
      const button = widget.querySelector('.goog-te-combo');
      button.setAttribute('style', `
        border: none;
        padding: 0;
        margin-right: 10px;
        background: #f9f9f9;
        border-radius: 3px;
        box-shadow: none;
        font-size: 16px;
        font-weight: bold;
        color: #333;
        cursor: pointer;
        transition: all 0.2s ease-in-out;
      `);
      button.addEventListener('mouseover', () => {
        button.style.background = '#e3e3e3';
      });
      button.addEventListener('mouseout', () => {
        button.style.background = '#f9f9f9';
      });

      // Style the language options
      const options = widget.querySelector('.goog-te-menu2');
      options.setAttribute('style', `
        padding: 10px;
        border: none;
        box-shadow: 0 2px 6px rgba(0,0,0,0.1);
        border-radius: 3px;
        background: #fff;
        font-size: 16px;
        font-weight: normal;
        color: #333;
      `);
      options.querySelectorAll('div').forEach((el) => {
        el.setAttribute('style', `
          margin: 0;
          padding: 5px 10px;
          cursor: pointer;
          transition: all 0.2s ease-in-out;
        `);
        el.addEventListener('mouseover', () => {
          el.style.background = '#e3e3e3';
        });
        el.addEventListener('mouseout', () => {
          el.style.background = '#fff';
        });
      });
    }
  }, []);

  return (
    <div id="google_translate_element"></div>
  );
};

export default GoogleTranslateWidget;
