import React, { useState, useEffect } from 'react';
import '../../App.css';
import styles from './staff-card.module.css';

const StaffCard = (item) => {
    const isLocalLink = item.portfolio.startsWith('../portfolios/');

    const [portfolioLink, setPortfolioLink] = useState(null);

    useEffect(() => {
      if (isLocalLink) {
        fetch(item.portfolio)
          .then(response => response.blob())
          .then(blob => {
            const url = URL.createObjectURL(blob);
            setPortfolioLink(url);
          });
      } else {
        setPortfolioLink(item.portfolio);
      }
    }, [isLocalLink, item.portfolio]);

    return (
        <div id="staff_card">
            {portfolioLink && (
              <a href={portfolioLink} className={styles.ptl}>
                <div className={styles.card}>
                    <div className={styles.img1}>
                        <img alt="" src={item.image} ></img>
                    </div>
                    <div className={styles.info}>
                        <p className={`${styles.name} ${styles.reddy}`}>{item.name}</p>
                        <p className={styles.position}>{item.position}</p>
                        <p className={`${styles.email} ${styles.reddy}`}>{item.email}</p>
                        <p className={styles.number}><span>+{item.number}</span></p>
                    </div>
                </div>
              </a>
            )}
        </div>
    );
}

export default StaffCard;
