import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './NewsBoard.css'

function NewsBoard() {
  const [news, setNews] = useState([]);

  useEffect(() => {
    axios.get('http:/localhost:5001/api/news').then((response) => {
      setNews(response.data);
    });
  }, []);

  return (
    <div>
      <h1>Жанылыктар</h1>
      {news.map((article) => (
        <div key={article._id}>
          {/* <img
            src={article.image}
            alt="news"
          /> */}
          <h2>{article.title}</h2>
          <p>{article.content}</p>
        </div>
      ))}
    </div>
  );
}

export default NewsBoard;
